import React from 'react';
import { Feather } from '@expo/vector-icons';
import { View, FlatList, Text, TouchableOpacity } from 'react-native';

import styles from './styles';


export default function Home( { navigation } ) {
  return (
    <View style={styles.container}>
    
    <Text style={styles.titulo}>Bem-vindo!</Text>
    <Text style={styles.descricao}>Projetos que você fez a diferença.</Text>

    <FlatList 
    data={[1, 2, 3, 4, 5]}
    style={styles.listaProjetos}
    keyExtractor={projeto => String(projeto)}
    renderItem={() => (
      <View style={styles.projeto}>
        <Text style={styles.projetoTipo}>Intituição/Projeto:</Text>
        <Text style={styles.projetoNome}>GPACI</Text>

        <Text style={styles.projetoTipo}>Atividade:</Text>
        <Text style={styles.projetoNome}>Aulas de música</Text>

        <Text style={styles.projetoTipo}>Descrição:</Text>
        <Text style={styles.projetoNome} numberOfLines={2}>Realizar aulas de canto com as crianças. Ajudando elas durante o processo de tratamento</Text>

        <TouchableOpacity style={styles.botaoDetalhes} onPress={() => { navigation.navigate("Details") }}>
          <Feather name='plus-circle' size={28} color='#2AB940'/>
        </TouchableOpacity>
    </View>
    )}
    />

    
  </View>
  );
}
