import * as React from 'react';
import { Text, View } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import Icon from 'react-native-vector-icons/FontAwesome'
import Home from '../pages/Home';
import Perfil from '../pages/Perfil';

  
const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

export default function Template() {

  return (
      <Stack.Navigator
        screenOptions={{
          gestureEnabled: true,
          gestureDirection: "horizontal",
          headerShown: false
       }}
       headerMode="float">
        <Stack.Screen name="Perfil" component={TabNavigation} />
        <Stack.Screen name="Home" component={TabNavigation} />
      </Stack.Navigator>
  );
}

 const TabNavigation = ({navigation,route}) => {
    navigation.setOption({titleHeader: getHeaderTitle(route)})

   return(
    <Tab.Navigator
        screenOptions={({ route }) => ({tabBarIcon: ({ color, size }) => {
            let iconName;
            if (route.name === 'Home') {iconName = 'home';} 
            if (route.name === 'Perfil') {iconName = 'user';} 
            
            return <Icon name={iconName} size={size} color={color} />;
          },
        })}
        tabBarOptions={{
          activeTintColor: 'orange',
          inactiveTintColor: 'gray',
        }}
        >
            <Tab.Screen name="Perfil" component={Perfil} />
            <Tab.Screen name="Home" component={Home} />

        </Tab.Navigator>
    );
}
