import React, { useState, useEffect } from 'react';
import { useNavigation } from '@react-navigation/native';
import {
  KeyboardAvoidingView,
  Image,
  View,
  TextInput,
  TouchableOpacity,
  Text,
  Animated,
  Keyboard
} from 'react-native';

import Styles from './styles';

export default function Login() {
  const navigation = useNavigation();

  function navigateToHome() {
    navigation.navigate('Root')
  }

  const [offset] = useState(new Animated.ValueXY({ x: 0, y: 95 }))
  const [opacity] = useState(new Animated.Value(0))
  const [logo] = useState(new Animated.ValueXY({ x: 250, y: 280 }))

  useEffect(() => {
    keyboardDidShowListener = Keyboard.addListener('keyboardDidShow', keyboardDidShow)
    keyboardDidHideListener = Keyboard.addListener('keyboardDidHide', keyboardDidHide)

    Animated.parallel([
      Animated.spring(offset.y, {
        toValue: 0,
        speed: 4,
        bounciness: 30
      }),
      Animated.timing(opacity, {
        toValue: 1,
        duration: 200
      })
    ]).start();
  }, []);

  function keyboardDidShow() {

    Animated.parallel([
      Animated.timing(logo.x, {
        toValue: 195,
        duration: 100
      })
    ]).start();

    Animated.parallel([
      Animated.timing(logo.y, {
        toValue: 225,
        duration: 100
      })
    ]).start();

  }

  function keyboardDidHide() {

    Animated.parallel([
      Animated.timing(logo.x, {
        toValue: 250,
        duration: 100
      })
    ]).start();

    Animated.parallel([
      Animated.timing(logo.y, {
        toValue: 280,
        duration: 100
      })
    ]).start();
  }

  return (
    <KeyboardAvoidingView behavior="height" style={Styles.background}>

      <View style={Styles.containerLogo}>
        <Animated.Image
          style={{
            width: logo.x,
            height: logo.y
          }}
          source={require('../../../src/assets/logo.jpg')}
        />
      </View>

      <Animated.View
        style={[
          Styles.container,
          {
            opacity: opacity,
            transform: [
              { translateY: offset.y }
            ]
          }
        ]}>

        <TextInput
          style={Styles.input}
          placeholder="Email"
          autoCorrect={false}
          onChangeText={() => { }}
        />

        <TextInput
          style={Styles.input}
          placeholder="Senha"
          autoCorrect={false}
          onChangeText={() => { }}
        />


        <TouchableOpacity style={Styles.btnSubmit} onPress={navigateToHome}>
          <Text style={Styles.submitText}>Acessar</Text>
        </TouchableOpacity>

        <TouchableOpacity style={Styles.btnRegister}>
          <Text style={Styles.registerText}>Criar Conta</Text>
        </TouchableOpacity>

      </Animated.View>

    </KeyboardAvoidingView>
  );
}
