import React, { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, Picker } from 'react-native';
import CardStack, { Card } from 'react-native-card-stack-swiper';
import CardItem from './components/CardItem';
import styles from './assets/styles/styles';
import Demo from './assets/data/demo.js';
import { Feather } from '@expo/vector-icons';

export default function ExploreProjects({ navigation }) {

  return (
    <View style={styles.bg}>
      <View style={styles.containerHome}>
        <View style={styles.top}>
          <TouchableOpacity onPress={() => { navigation.navigate("SearchProjects") }} style={styles.loadButton}>
            <Feather name="search" size={20} color="#FFF" />
          </TouchableOpacity>
        </View>

        <CardStack
          loop={true}
          verticalSwipe={false}
          renderNoMoreCards={() => null}
          ref={swiper => (this.swiper = swiper)}
        >
          {Demo.map((item, index) => (
            <Card key={index}>
              <CardItem
                image={item.image}
                name={item.name}
                description={item.description}
                matches={item.match}
                actions
                onPressLeft={() => this.swiper.swipeLeft()}
                onPressRight={() => this.swiper.swipeRight()}
              />
            </Card>
          ))}
        </CardStack>
      </View>
    </View>
  );
};
