import React from 'react';
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { View, FlatList, Image, Text, TouchableOpacity, TextInput } from 'react-native';

import { Searchbar } from 'react-native-paper';

import styles from './assets/styles/searchPageStyle';

export default function SearchProjects() {
  const navigation = useNavigation();

  function navigateBack() {
    navigation.goBack()
  }



  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={navigateBack} style={{ marginRight: 15 }}>
          <Feather name='arrow-left-circle' size={28} color='#2AB940' />
        </TouchableOpacity>
        <TextInput
          style={styles.searchInput}
          placeholder="Buscar novos projetos..."
          placeholderTextColor="#999"
          autoCapitalize="words"
          autoCorrect={false}
        />
        <TouchableOpacity onPress={() => { }} style={styles.loadButton}>
          <Feather name="search" size={20} color="#FFF" />
        </TouchableOpacity>
      </View>

      <FlatList
        data={[1, 2, 3, 4, 5]}
        showsVerticalScrollIndicator={false}
        style={styles.listaProjetos}
        keyExtractor={projeto => String(projeto)}
        renderItem={() => (
          <View style={styles.projeto}>
            <TouchableOpacity>
            <Text style={styles.projetoTipo}>Intituição/Projeto:</Text>
            <Text style={styles.projetoNome}>GPACI</Text>

            <Text style={styles.projetoTipo}>Atividade:</Text>
            <Text style={styles.projetoNome}>Aulas de música</Text>

            <Text style={styles.projetoTipo}>Descrição:</Text>
            <Text style={styles.projetoNome} numberOfLines={2}>Realizar aulas de canto com as crianças. Ajudando elas durante o processo de tratamento</Text>

            

            </TouchableOpacity>
          </View>
        )}
      />



    </View>
  );
};
