import React, { Component } from 'react';
import { Api } from '../../services/api';
import { Text, View, Image, TouchableOpacity,FlatList, Dimensions, StyleSheet, Button } from 'react-native';
import img from './odsImage';
import s from './style'
import { FontAwesome, Feather } from '@expo/vector-icons';
import Constants from 'expo-constants';

const numColumns = 3;
const numStars = 5;

export default class DetalhesOds extends Component{
      constructor(props){
        super(props);
        this.state ={
           isLoading: true,
           resultData: {
            starRating: 4,
           },
           filled: false
        }
    } 
      
      render() {
        let stars = [];
        const { data } = this.props.route ? this.props.route.params : null;
        
        for(let x = 1; x <= numStars; x++){
          stars.push(
            <Star filled={x <= this.state.resultData.starRating ? true : false}/>
          )
        }
       
        return (
        <View style={styles.container}>
            <View style={styles.cardImg}>
                <Image source={img.obs[data.imageName]} style={styles.odsImg}/>
            </View>
            <TouchableOpacity style={s.btnHome} onPress={() => {this.props.navigation.navigate('Ods');}}>
                <Feather name='arrow-left-circle' size={22} color='green'></Feather>
            </TouchableOpacity>
            <View style={{flexDirection:"row"}}>{stars}</View>
            

            <FlatList
                data={[1]}
                keyExtractor={projeto => String(projeto)}
                renderItem={() => (
                    <View>
                        <View style={styles.listContainer}>
                            <Text style={styles.projetoTipo}>Descrição:</Text>
                            <Text style={styles.projetoNome}>{data.descricao}.</Text>
                        </View>
                        <View style={styles.listContainer}>
                            <Text style={styles.projetoTipo}>Intitutos:</Text>
                            <Text style={styles.projetoNome}>{'1 - teste.\n2 - teste.\n3 - teste.\n4 - teste.\n5 - teste.\n6 - teste.\n7 - teste.'}</Text>
                        </View>
                        <View style={styles.listContainer}>
                            <Text style={styles.projetoTipo}>Ods:</Text>
                            <Text style={styles.projetoNome}>
                            testando flastlist para configurações futuras.
                            testando flastlist para configurações futuras.
                            testando flastlist para configurações futuras.
                            testando flastlist para configurações futuras.
                            testando flastlist para configurações futuras.
                            </Text>
                        </View>
                        <View style={styles.compartilhar}>
                            <Text style={styles.projetoTipo}>Compartilhar:</Text>
                            <TouchableOpacity style={styles.btnCompartilhar}>
                              <Text style={{color: "white"}}>Facebook</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                )} />
        </View>
      )
    }
}
class Star extends Component{

  render(){
    return (
      <FontAwesome style={styles.starsRating} name={this.props.filled === true ? "star" : "star-o"} size={25} color="#edd318"/>
    )
  }
}

const styles = StyleSheet.create({
   container: {
        flex: 1,
        paddingTop: Constants.statusBarHeight,
        alignItems: "center",
   },
   cardImg:{
      justifyContent: "center",
      alignItems: "center",
      minHeight: 150,
      maxHeight: 150,
      width: '100%',
   },
   odsImg:{
     position: 'absolute',
     height: '100%',
     width: '100%',
   },
   listContainer: {
    margin: 10,
    padding: 15,
    borderRadius: 10,
    backgroundColor: '#fff',
    marginBottom: 16,
    borderWidth: 2,
    borderColor: 'gray',
  },
  projetoTipo: {
    fontSize: 14,
    color: '#41414d',
    fontWeight: 'bold'
  },
  projetoNome: {
    marginTop: 8,
    fontSize: 15,
    marginBottom: 16,
    color: '#737380'
  }, 
  starsRating:{
    alignItems: "center",
    marginHorizontal: 6,
  },
  btnCompartilhar:{
    backgroundColor:"#0059ff",
    alignItems: "center",
    borderWidth: 1,
    height: 42,
    width: "50%",
    justifyContent: "center",
    borderRadius: 20
  },
  compartilhar:{
    margin: 10,
    padding: 15,
    borderRadius: 10,
    backgroundColor: '#fff',
    marginBottom: 16,
    borderWidth: 2,
    borderColor: 'gray',
    alignItems:"center",
     bottom: 10
  } 
});
