import { StyleSheet, Dimensions } from "react-native";

const DIMENSION_WIDTH = Dimensions.get("window").width;
const DIMENSION_HEIGHT = Dimensions.get("window").height;

export default StyleSheet.create({
	containerCardItem: {
		top: 30,
		backgroundColor: "#FFFFFF",
		borderRadius: 8,
		alignItems: "center",
		margin: 10,
		shadowOpacity: 0.05,
		shadowRadius: 10,
		shadowColor: "#000",
		shadowOffset: { height: 4, width: 4 }, elevation: 4
	},
	descriptionCardItem: {
		marginHorizontal: 2,
		color: "#757E90",
		textAlign: "center"
	},

	actionsCardItem: {
		flexDirection: "row",
		alignItems: "center",
		paddingVertical: 30
	},
	button: {
		width: 60,
		height: 60,
		borderRadius: 30,
		backgroundColor: "#FFFFFF",
		marginHorizontal: 7,
		alignItems: "center",
		justifyContent: "center",
		shadowOpacity: 0.15,
		shadowRadius: 20,
		shadowColor: "#000",
		shadowOffset: { height: 10, width: 0 }
	},

	like: {
		color: "#B644B2"
	},
	dislike: {
		color: "#363636"
	},

	filters: {
		top: 10,
		backgroundColor: "#FFFFFF",
		padding: 10,
		borderRadius: 20,
		width: 70,
		shadowOpacity: 0.05,
		shadowRadius: 10,
		shadowColor: "#000",
		shadowOffset: { height: 0, width: 0 }
	},
	filtersText: {
		color: "#363636",
		fontSize: 13,
		textAlign: "center"
	},

	bg: {
		flex: 1,
		width: DIMENSION_WIDTH,
		height: DIMENSION_HEIGHT
	},
	top: {
		paddingTop: '12%',
		marginHorizontal: 10,
		flexDirection: "row",
		justifyContent: "flex-end",
		alignItems: "center"
	},
	title: { paddingBottom: 10, fontSize: 22, color: "#363636" },
	icon: {
		fontSize: 20,
		color: "#363636",
		paddingRight: 10
	},

	containerHome: { marginHorizontal: 10 },

	searchInput: {
		flex: 1,
		height: 50,
		backgroundColor: "#fff",
		color: "#363636",
		borderRadius: 25,
		paddingHorizontal: 20,
		fontSize: 16,
		shadowColor: "#000",
		shadowOffset: {
			width: 4,
			height: 4,
		},
		elevation: 4,
	},
	loadButton: {
		width: 50,
		height: 50,
		backgroundColor: "#B644B2",
		borderRadius: 25,
		justifyContent: "center",
		alignItems: "center",
		marginLeft: 15,
		shadowColor: "#000",
		shadowOffset: {
			width: 4,
			height: 4,
		},
		elevation: 4,
	}

});
