import React from 'react';
import { Text, View, Image, TouchableOpacity } from 'react-native';
import s from './style';
import { Feather } from '@expo/vector-icons';
import {useNavigation} from '@react-navigation/native';
import Notificacao from '../Notificacao';


export default function Perfil() {
  const navigation = useNavigation()

  function navigateToEdit(route){

      switch(route){
        case 'Notificacao':
          return navigation.navigate('Notificacao');
      }
  
  }  

  return (
    <View style={s.container}>
      <Image source={require('../../assets/leaf.jpg')} style={s.imgBackground}></Image>
      <TouchableOpacity style={s.btnEditPerfil} onPress={()=> {this.navigateToEdit('Notificacao')}}>
        <Feather name='settings' size={22} color='#2AB940'></Feather>
      </TouchableOpacity>
      <TouchableOpacity style={s.btnNotificacao}>
      <Feather name='bell' size={22} color='#2AB940'></Feather>
      </TouchableOpacity>
      <TouchableOpacity style={s.btnOds} >
      <Feather name='star' size={22} color='#2AB940'></Feather>
      </TouchableOpacity>
      <TouchableOpacity style={s.btnLagout} >
      <Feather name='log-out' size={22} color='white'></Feather>
      </TouchableOpacity>

    <View >
      <Image style={s.circleImg} source={require('../../assets/avatar.png')}/>
      <TouchableOpacity style={s.btnTrocarImg} >
      <Feather name='camera' size={16} color='#2AB940'></Feather>
      </TouchableOpacity>
    </View>
    <Text style={s.userName}> Jo√£o de Narnia </Text>
    <Text style={s.userSubTitulo}> joaozin_narnia_2020@gmail.com </Text>
  </View>
  );
}
