import React, { Component } from 'react';
import {Alert} from 'react-native';
import {
  KeyboardAvoidingView,
  View,
  TextInput,
  TouchableOpacity,
  Text,
  Keyboard
} from 'react-native';

import Styles from './styles';

import { Api } from '../../services/api';
import * as Auth from '../../services/auth';

export default class CreateAccount extends Component {

  state = {
    user: '',
    email: '',
    password: ''
  }

  create = async (email, pass) => {
    const teste = await Auth.signInAsync(email, pass);
    //new Api('user')
      //.post({ email: this.state.email, password: this.state.password })
      //.then(res => {
       // Alert.alert('Funcionou');
      //})
     // .catch(err => err);
  }

  render() {

    return (
      <KeyboardAvoidingView behavior="height" style={Styles.background} >

        <View
          style={Styles.container}>

          <TextInput
            style={Styles.input}
            placeholder="Email"
            autoCorrect={false}
            onChangeText={text => this.setState({ email: text })}
          />

          <TextInput
            style={Styles.input}
            placeholder="Senha"
            autoCorrect={false}
            onChangeText={text => this.setState({ password: text })}
          />


          <TouchableOpacity style={Styles.btnSubmit} onPress={() => this.create(this.state.email, this.state.password)}>
            <Text style={Styles.submitText}>Criar</Text>
          </TouchableOpacity>

        </View>

      </KeyboardAvoidingView>
    );
  }
}
