import React from 'react';
import s from './style';
import { View, FlatList, Text, TouchableOpacity } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import Perfil from '../Perfil';


export default function Notificacao({ navigation }) {

    return (
        <View style={s.container}>
            <TouchableOpacity onPress={() => {navigation.navigate('Perfil');}}>
                <Feather name='arrow-left-circle' size={28} color='#2AB940'></Feather>
            </TouchableOpacity>
            <Text style={s.titulo}>Notificações</Text>

            <FlatList
                data={[1, 2, 3, 4, 5]}
                style={s.listaProjetos}
                keyExtractor={projeto => String(projeto)}
                renderItem={() => (
                    <View>
                        <View style={s.notificacaoAceito}>
                            <Text style={s.projetoTipo}>Descrição:</Text>
                            <Text style={s.projetoNome} numberOfLines={3}>Seu pedido foi aceito com sucesso, enter em contato para ter mais detalhes.</Text>
                        </View>
                        <View style={s.notificacaoRecusado}>
                            <Text style={s.projetoTipo}>Descrição:</Text>
                            <Text style={s.projetoNome} numberOfLines={2}>Desculpe, mas seu pedido foi recusado.</Text>
                        </View>
                    </View>
                )} />
        </View>
    );
}
