import React, { Component } from 'react';
import {
    KeyboardAvoidingView, TextInput, Image, View, Button, FlatList, Alert, Platform,
    StyleSheet,
    Text,
    ScrollView,
    Switch,
    TouchableWithoutFeedback,
    TouchableOpacity,
    ActivityIndicator,
    Dimensions,
    LayoutAnimation,
} from 'react-native';


import { TextInputMask } from 'react-native-masked-text'
import { Feather } from '@expo/vector-icons';
import Styles from './style';
import SectionedMultiSelect from 'react-native-sectioned-multi-select';
import { Colors } from 'react-native/Libraries/NewAppScreen';
const img = require('./z.jpg');

const items = [
    {
        title: 'Musica',
        id: 0,

        children: [
            {
                title: 'Viol�o',
                id: 10,
            },
            {
                title: 'Teclado',
                id: 11,
            },
            {
                title: 'Violino',
                id: 13,
            },
        ],
    },
    {
        title: 'Esportes',
        id: 1,
        icon: 'cake',
        children: [
            {
                title: 'Luta',
                id: 26,
            },
            {
                title: 'Atletismo',
                id: 27,
            },
            {
                title: 'Dan�a',
                id: 28,
            },
            {
                title: 'Ginastica',
                id: 29,
            },
        ],
    }
]

export default class Login extends Component {
    constructor() {
        super()
        this.state = {
            selectedItems: [],
        };
    }
    onSelectedItemsChange = (selectedItems) => {
        this.setState({ selectedItems });
    };
    render() {
        return (
            <ScrollView>
                <KeyboardAvoidingView behavior="height" style={Styles.background}>
                    <TextInput
                        style={Styles.input}
                        placeholder="Nome Completo"
                        autoCorrect={false}
                        onChangeText={() => { }}
                    />

                    <TextInput
                        style={Styles.input}
                        selectionColor='#737380'
                        placeholder="E-mail"
                        autoCorrect={false}
                        onChangeText={() => { }}
                    />

                    <TextInputMask
                        type={'cpf'}
                        style={Styles.input}
                        placeholder="CPF"
                        value={this.state.cpf}
                        onChangeText={text => {
                            this.setState({
                                cpf: text
                            })
                        }}
                    />

                    <TextInputMask
                        type={'custom'}
                        placeholder="RG"
                        options={{
                            mask: 'SS.SSS.SSS-SS'
                        }}
                        value={this.state.text}
                        onChangeText={text => {
                            this.setState({
                                text: text
                            })
                        }}
                        style={Styles.input}
                    />

                    <TextInputMask
                        type={'cel-phone'}
                        style={Styles.input}
                        placeholder="Telefone"
                        value={this.state.phone}
                        onChangeText={text => {
                            this.setState({
                                phone: text
                            })
                        }}
                    />

                    <TextInputMask
                        type={'cel-phone'}
                        style={Styles.input}
                        placeholder="Celular"
                        value={this.state.celular}
                        onChangeText={text => {
                            this.setState({
                                celular: text
                            })
                        }}
                    />

                    <TextInputMask
                        type={'datetime'}
                        style={Styles.input}
                        placeholder="Data Nascimento"
                        options={{
                            format: 'DD/MM/YYYY'
                        }}
                        value={this.state.dt}
                        onChangeText={text => {
                            this.setState({
                                dt: text
                            })
                        }}
                    />

                    <TextInput
                        style={Styles.input}
                        selectionColor='#737380'
                        placeholder="CEP"
                        autoCorrect={false}
                        onChangeText={() => { }}                        
                    />

                    <View style={{flexDirection: 'row'}}> 
                        <TextInput
                            style={Styles.inputOrient}
                            selectionColor='#737380'
                            placeholder="Endereço"
                            autoCorrect={false}
                            onChangeText={() => { }}
                        />

                        <TextInput
                            style={Styles.inputOrient2}
                            selectionColor='#737380'
                            placeholder="Nº"
                            autoCorrect={false}
                            onChangeText={() => { }}
                        />
                    </View>

                    <TextInput
                            style={Styles.input}
                            selectionColor='#737380'
                            placeholder="Cidade"
                            autoCorrect={false}
                            onChangeText={() => { }}
                    />

                    <View style={{flexDirection: 'row'}}> 
                        <TextInput
                            style={Styles.inputOrient}
                            selectionColor='#737380'
                            placeholder="Bairro"
                            autoCorrect={false}
                            onChangeText={() => { }}
                        />

                        <TextInput
                            style={Styles.inputOrient2}
                            selectionColor='#737380'
                            placeholder="Estado"
                            autoCorrect={false}
                            onChangeText={() => { }}
                        />
                    </View>

                    <SectionedMultiSelect
                        items={items}
                        ref={(SectionedMultiSelect) => (this.SectionedMultiSelect = SectionedMultiSelect)}
                        displayKey="title"
                        uniqueKey="id"
                        subKey="children"
                        confirmText="Selecionar"
                        selectText="Escolha algumas op��o"
                        showDropDowns={true}
                        readOnlyHeadings={true}
                        onSelectedItemsChange={this.onSelectedItemsChange}
                        showCancelButton={true}
                        showRemoveAll={true}
                        colors={{ primary: '#2AB940', success: '#2AB940' }}
                        selectedItems={this.state.selectedItems}
                        styles={{
                            selectedItem: {
                                backgroundColor: 'rgba(0,0,0,0.1)',
                            },
                            selectedSubItem: {
                                backgroundColor: 'rgba(0,0,0,0.1)',
                            },
                            scrollView: { paddingHorizontal: 0 },
                        }}
                    />

                    <View style={Styles.action}>
                        <TouchableOpacity style={Styles.btnSalvar}>
                            <Feather name='save' size={22} color='white'></Feather>
                        </TouchableOpacity>

                        <TouchableOpacity style={Styles.btnCancelar} onPress={() => { this.props.navigation.goBack() }}>
                            <Feather name='minus' size={22} color='white'></Feather>
                        </TouchableOpacity>
                    </View>
                </KeyboardAvoidingView>
            </ScrollView>
        );
    }
}
